//***** PRODUCTION enveloppe pour un jour type
// centrés sur 13h00, prodmoy (CS=1, jour de 12h) et prodmax (CS = Pprod en kWc, jour de 14h)
//A vérifier : decalage horaire avec GMT ??
var prodmoy = Math.max((((dateNow - millidate)/(1000*60*60)) * (puis/2 * puis * 1000/2.3955 * (-1 * (Math.pow(((((hours*60+minutes)/60)-13)/7),2)) + 1))), 0);
var prodmax = Math.max((((dateNow - millidate)/(1000*60*60)) * (puis * puis * 1000/2.3955 * (-1 * (Math.pow(((((hours*60+minutes)/60)-13)/7),2)) + 1))), 0);

//*****FORMULE ADAPTEE AU PROFIL ENEDIS => PRENDS EN COMPTE SunCal
// Interpolation linéaire approximative entre 2 dates pour former le graphique

var CS = SunCal[mois][4];
var CS2 = SunCal[mois2][4];

CS = CS + ((21-jour)*(CS-CS2)/Math.abs(SunCal[mois2][0] - SunCal[mois][0]));

//17Avril2019 timeNow.getTime
var Jour100 = Math.round(100*(timeNow.getTime() - LeverSoleil.getTime())/(CoucherSoleil.getTime() - LeverSoleil.getTime()));
var parabole = 1 + (-1 * (Math.pow(((Jour100-50)/50 ), 2))); // parabole <0 LA NUIT avant et après le coucher du soleil
var facteurX = 3047.380505; //facteur  arbitraire sans unité calculé à partir de la prod moyenne d'une journee 

var facteurP = puis * productible * facteurX /(365*24); // facteur de puissance sans unité
var DeltaT = (dateNow - millidate)/(1000*60*60); //durée en fraction d'heure pour calculer des kWh par la suite => vaut env. 4000/(1000*60*60) à 15000/(1000*60*60)
prod = Math.max((DeltaT * facteurP * parabole * CS), 0); // 0 avant et après le coucher du soleil car parabole <0 la nuit

//evacuer les valeur abérentes (inititalisation);
if (Math.abs(prod) > membre.Pprod*1000) {prod = 0; soutir = 0;inject = 0;conso = 0;}
if (Math.abs(soutir) > membre.Psoutir*1000) {prod = 0; soutir = 0;inject = 0;conso = 0;}
if (Math.abs(inject) > membre.Pprod*1000) {prod = 0; soutir = 0;inject = 0;conso = 0;}
if (Math.abs(conso) > membre.Psoutir*1000){prod = 0; soutir = 0;inject = 0;conso = 0;}

// Calcul des variables de la courbe à afficher

if (prod <= 0) // LA NUIT
    {prod = 0;
    autoconso = 0;
    inject = 0;
    soutir = conso;
    }
    else {if (prod < conso) 
            {soutir = conso - prod;
            inject = 0;
            autoconso = prod;
            }
            else  // prod > conso CAS GENERAL
            {soutir = 0;
            autoconso =  conso;
            inject = prod - conso;
            }
        }


